mapper.select:
SELECT ROW_NUMBER() over(ORDER BY p.pet_no) AS pno, p.*, REGEXP_REPLACE(p.owner_tel, '(02|.{3})(.+)(.{4})', '\\1-\\2-\\3') as s_Tel FROM ( SELECT * FROM petview
WHERE pet_name LIKE CONCAT('%',#{pet_search}, '%') OR owner_name LIKE CONCAT('%',#{pet_search}, '%') OR owner_tel LIKE CONCAT('%',#{pet_search}, '%') OR pet_birth LIKE CONCAT('%',#{pet_search}, '%')
) p
