mapper.update:
UPDATE medicalList SET
medical_name=#{medical_name}, medical_price=#{medical_price}
WHERE
medical_no =#{medical_no};

UPDATE vaccine SET
vac_name=#{vac_name}, vac_price=#{vac_price},
vac_cycleY=#{vac_cycleY},vac_cycleM=#{vac_cycleM},vac_cycleW=#{vac_cycleW}
WHERE vac_no =#{vac_no};

UPDATE petType SET
type_name=#{type_name}
WHERE type_no=#{type_no};
