mapper.select:
SELECT ROW_NUMBER() over(order by m.medical_no desc) AS mno,m.* FROM ( SELECT * FROM medicalList WHERE medical_category=#{category}
AND medical_name LIKE CONCAT('%', #{search_value},'%')
) m ORDER BY mno LIMIT #{pagenum},#{contentnum}

SELECT COUNT(*) FROM ${table}

	WHERE medical_category= #{category}


			AND medical_name LIKE CONCAT('%',
			#{search_value},'%')






			WHERE vac_name LIKE CONCAT('%',#{search_value},'%')






			WHERE type_name LIKE CONCAT('%',#{search_value},'%')

SELECT ROW_NUMBER() over(ORDER BY v.vac_no) AS vno,v.* FROM ( SELECT * FROM vaccine
WHERE vac_name LIKE CONCAT('%', #{search_value},'%')
) v ORDER BY vno DESC LIMIT #{pagenum}, #{contentnum};

SELECT ROW_NUMBER() over(ORDER BY t.type_no) AS tno,t.* FROM ( SELECT * FROM petType
WHERE type_name LIKE CONCAT('%', #{search_value},'%')
) t ORDER BY tno LIMIT #{pagenum}, #{contentnum};

mapper.update:
UPDATE medicalList SET
medical_name=#{medical_name}, medical_price=#{medical_price}
WHERE
medical_no =#{medical_no};

UPDATE vaccine SET
vac_name=#{vac_name}, vac_price=#{vac_price},
vac_cycleY=#{vac_cycleY},vac_cycleM=#{vac_cycleM},vac_cycleW=#{vac_cycleW}
WHERE vac_no =#{vac_no};

UPDATE petType SET
type_name=#{type_name}
WHERE type_no=#{type_no};
