mapper.select:
SELECT ROW_NUMBER() over(ORDER BY p.pet_name) AS pno, p.*,REGEXP_REPLACE(p.owner_tel, '(02|.{3})(.+)(.{4})', '\\1-\\2-\\3') as o_Tel FROM ( SELECT * FROM petview WHERE pet_del = 1
AND (pet_name LIKE CONCAT('%',#{search_value}, '%') OR owner_name LIKE CONCAT('%',#{search_value}, '%') OR owner_tel LIKE CONCAT('%',#{search_value}, '%'))
) p
