mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY chart_no ) AS rownum , pay_no, listpay_date, pay_yn, totalPrice, chart_no, owner_name, pet_name FROM paydetailview
WHERE format_date BETWEEN #{fromDate} AND #{toDate}
WHERE pet_name LIKE CONCAT('%',#{search_value},'%') OR owner_name LIKE CONCAT('%',#{search_value},'%') OR chart_no LIKE CONCAT('%',#{search_value},'%')
WHERE pet_name LIKE CONCAT('%',#{search_value},'%')
WHERE owner_name LIKE CONCAT('%',#{search_value},'%') WHERE chart_no LIKE CONCAT('%',#{search_value},'%')
WHERE format_date = #{todayDate}
WHERE pay_yn = #{pay_yn}
ORDER BY rownum DESC LIMIT #{pagenum}, #{contentnum};

        SELECT COUNT(*) FROM paydetailview

		 WHERE format_date BETWEEN #{fromDate} AND #{toDate}



				WHERE pet_name LIKE CONCAT('%',#{search_value},'%') 
				WHERE owner_name LIKE CONCAT('%',#{search_value},'%')
				WHERE chart_no LIKE CONCAT('%',#{search_value},'%') 



		WHERE format_date = #{todayDate}

SELECT p.*
FROM paydetailview p
WHERE pay_no =#{payNo};

SELECT medical_category, medical_subcate, medical_name, medicaldata_ea, medical_price, staff_name
FROM  medicaldataview
