mapper.select:
SELECT * FROM clientview WHERE owner_no=#{detail_no }
INSERT INTO reservation SET reservation_date = #{reservation_date}, reservation_memo = #{reservation_memo }, reservation_yn = 'y', reservation_date = #{reservation_date}, reservation_memo = #{reservation_memo }, reservation_time=NOW(), staff_no = (SELECT staff_no FROM staff WHERE staff_id = #{staff_id} ), pet_no = #{petNo};
SELECT * FROM reservationview WHERE reserv_date = CURDATE() AND (reservation_yn = 'n' OR reservation_yn = '') ORDER BY reservation_date DESC
SELECT ROW_NUMBER() over(ORDER BY r.reserv_date) AS rno,r.pet_name,r.owner_name,r.reserv_time FROM (SELECT * FROM reservationview WHERE DATE_FORMAT(reservation_date, "%Y-%m-%d") = CURDATE()) r ORDER BY rno
SELECT ROW_NUMBER() over(ORDER BY i.receive_time) AS ino,i.pet_name,i.owner_name,i.receive_state,i.pay_yn FROM (SELECT * FROM indexreceivepay WHERE DATE_FORMAT(receive_time, "%Y-%m-%d") = CURDATE()) i ORDER BY ino
SELECT * FROM receptionview WHERE reserv_date = CURDATE() AND (reservation_yn = 'n' OR reservation_yn = '') ORDER BY reservation_date DESC
SELECT * FROM reservationview WHERE reservation_no = #{reservation_no} ORDER BY reservation_date DESC
UPDATE reservation SET reservation_date = #{update_reservation_date }, reservation_memo = #{update_reservation_memo } WHERE reservation_no=#{update_reservation_no}
DELETE FROM reservation WHERE reservation_no=#{delete_reservation_no }
