mapper.select:
SELECT ROW_NUMBER()
OVER(ORDER BY pay_date) AS rownum, pay_date, totalprice
FROM salesview

WHERE pay_date BETWEEN #{fromDate} AND #{toDate}

ORDER BY rownum DESC
LIMIT  #{pagenum}, #{contentnum};

SELECT totalprice , DATE_FORMAT(salesview.pay_date, '%m-%d') AS pay_date FROM salesview
WHERE pay_date BETWEEN #{fromDate} AND #{toDate}
ORDER BY pay_date
